import React, { useState } from "react";

function CreateArea(props) {
    const [inputTitle, setInputTitle] = useState("");
    const [inputContent, setInputContent] = useState("");

    function handleChangeTitle(event) {
        const newValue = event.target.value;
        setInputTitle(newValue);
      }

      function handleChangeContent(event) {
        const newValue = event.target.value;
        setInputContent(newValue);
      }

  return (
    <div>
      <form>
        <input onChange={handleChangeTitle} name="title" placeholder="Title" value={inputTitle} />
        <textarea onChange={handleChangeTitle} name="content" placeholder="Take a note..." rows="3" value={inputContent} />
        <button onClick={() => {
            props.onAdd(inputTitle, inputContent);
            setInputTitle("");
            setInputContent("");
          }}>Add</button>
      </form>
    </div>
  );
}

function InputArea(props) {
    const [inputText, setInputText] = useState("");

    function handleChange(event) {
      const newValue = event.target.value;
      setInputText(newValue);
    }

    return (
      <div className="form">
        <input onChange={handleChange} type="text" value={inputText} />
        <button
          onClick={() => {
            props.onAdd(inputText);
            setInputText("");
          }}
        >
          <span>Add</span>
        </button>
      </div>
    );
  }


export default CreateArea;
